mapper.select:
select *
from(select rownum idx, m.*
from (select *
from menu
order by menu_no) m
where 1=1

    and (menu_title like '%'||#{keyword}||'%' or menu_desc like '%'||#{keyword}||'%' or menu_url like '%'||#{keyword}||'%')


    ORDER BY #{filterType} #{ascDesc}

) afterm
 where idx >= #{contentBegin} and idx <= #{contentEnd}
